mapper.update:
update T_USER
set stutas = 1
 where id = #{id,jdbcType=INTEGER}

mapper.select:
	  select * FROM(select ROWNUM as ROWNO,t.* from (Select * from t_user where 1=1 and stutas=0  

      order by ID desc) t where  ROWNUM <= #{endRow,jdbcType=INTEGER})
      where ROWNO>= #{startRow,jdbcType=INTEGER}

select u.*,ur.role_id from t_user u
left join t_user_role ur on ur.user_id = u.id
where u.stutas=0 and  u.username = #{username,jdbcType=VARCHAR}
