mapper.select:

select bankname,fid
 from (select t.fdisplayname_l2 as bankname,t.fid as fid, rownum as rowno
         from t_bd_bank t
        where t.flevel = '1')
where rowno > #{fristResult}
  and rowno <= #{maxResult}


 select bankname,fid
 from (select t.fdisplayname_l2 as bankname,t.fid as fid, rownum as rowno
         from t_bd_bank t
        where t.fparentid =#{fid})
where rowno > #{fristResult}
  and rowno <= #{maxResult}
